            "instructions": [
                {
                    "display_text": "Roll out each slice of white bread with a rolling pin until flat.",
                    "start_time": 0,
                    "appliance": null,
                    "end_time": 0,
                    "temperature": null,
                    "id": 72636,
                    "position": 1
                },
                {
                    "temperature": null,
                    "id": 72637,
                    "position": 2,
                    "display_text": "In a small bowl, mix together the vanilla frosting and whipped topping until evenly combined.",
                    "start_time": 0,
                    "appliance": null,
                    "end_time": 0
                },
                {
                    "appliance": null,
                    "end_time": 0,
                    "temperature": null,
                    "id": 72638,
                    "position": 3,
                    "display_text": "Spread the frosting mixture evenly over the flattened bread slices.",
                    "start_time": 0
                },
                {
                    "temperature": null,
                    "id": 72639,
                    "position": 4,
                    "display_text": "Scoop 1 teaspoon of pineapple or strawberry jam on top of each slice of bread and spread evenly, then roll up the bread and filling.",
                    "start_time": 0,
                    "appliance": null,
                    "end_time": 0
                },
                {
                    "position": 5,
                    "display_text": "In a microwave-safe bowl, combine the chocolate chips and coconut oil. Microwave in 30-second intervals, stirring between, until melted and smooth.",
                    "start_time": 0,
                    "appliance": null,
                    "end_time": 0,
                    "temperature": null,
                    "id": 72640
                },
                {
                    "id": 72641,
                    "position": 6,
                    "display_text": "Dip the rolls in the chocolate sauce until completely coated, then refrigerate for 5 minutes, until the chocolate hardens.",
                    "start_time": 0,
                    "appliance": null,
                    "end_time": 0,
                    "temperature": null
                },
                {
                    "appliance": null,
                    "end_time": 0,
                    "temperature": null,
                    "id": 72642,
                    "position": 7,
                    "display_text": "Repeat to coat the rolls in another layer of chocolate (re-melt the chocolate in the microwave if needed), then refrigerate for another 5 minutes to set the second layer of chocolate.",
                    "start_time": 0
                },
                {
                    "temperature": null,
                    "id": 72643,
                    "position": 8,
                    "display_text": "Drizzle any remaining melted chocolate over the rolls, then refrigerate for 2–3 minutes, until hardened.",
                    "start_time": 0,
                    "appliance": null,
                    "end_time": 0
                },
                {
                    "end_time": 0,
                    "temperature": null,
                    "id": 72644,
                    "position": 9,
                    "display_text": "Serve immediately or store in an airtight container for up to 3 days.",
                    "start_time": 0,
                    "appliance": null
                },
                {
                    "start_time": 0,
                    "appliance": null,
                    "end_time": 0,
                    "temperature": null,
                    "id": 72645,
                    "position": 10,
                    "display_text": "Enjoy!"
                }
            ],
